import React from 'react';
import { Link } from 'react-router-dom';

const Order = () => {
  // Assuming cartItems are passed as state from Cart component
  const cartItems = [
    // Sample cart items data
   {"id": 1,
        "name": "Laptop",
        "description": "Dell 14 Laptop, 12th Gen Intel Core i3-1215U Processor/8GB/512GB SSD/Intel UHD Graphics/14.0 (35.56cm) FHD/Windows 11 + MSO 21/15 Month McAfee/Spill-Resistant Keyboard/Grey/Thin & Light 1.48kg",
        "price": "34990",
        "imageurl": "https://www.bing.com/th?id=OIP.A3BKzcvY32CLG3ruEOgj_gHaHa&w=249&h=249&c=8&rs=1&qlt=90&o=6&dpr=1.5&pid=3.1&rm=2"},
    
    {
        "id": 2,
        "name": "iphone",
        "description": "Apple iPhone 15 (128 GB) - Black",
        "price": 72690,
        "imageurl": "https://tse3.mm.bing.net/th/id/OIP.ht515q53Cq2gpImNQP0NUAHaHa?w=181&h=181&c=7&r=0&o=5&dpr=1.5&pid=1.7"
    },
    {
        "id": 6,
        "name": "Modem",
        "description": "D-Link DIR-825 1200Mbps Dual Band Wi-Fi Router | Fast & Reliable Speeds | 2.4 GHz up to 300Mbps & 5 GHz up to 867Mbps | Gigabit Ethernet Ports | High-Gain Antennas | Easy Setup",
        "price": 2030,
        "imageurl": "https://tse1.mm.bing.net/th/id/OIP.5rj0_r70QBS3uXYnJ6_Y_QHaHa?w=202&h=202&c=7&r=0&o=5&dpr=1.5&pid=1.7"
    },
    {
        "id": 5,
        "name": "Laptop Bag",
        "description": "MOKOBARA The Spacewalk Backpack Water-Resistant 15.6\" Laptop Bag with Luggage Sleeve for Work, Travel, and Daily Use",
        "price": 4999,
        "imageurl": "https://tse2.mm.bing.net/th/id/OIP.JKGWqYqT6zOY1N4nyP0DDAHaI0?w=187&h=220&c=7&r=0&o=5&dpr=1.5&pid=1.7"
    },
    {
        "id": 3,
        "name": "HeadPhone",
        "description": "HAMMER Bash Max Over The Ear Wireless Bluetooth Headphones with Mic, Touch Control, Deep Bass, Upto 18 Hours Playtime, Bluetooth 5.3, Workout/Travel (Black)",
        "price": 2399,
        "imageurl": "https://tse3.mm.bing.net/th/id/OIP.RKSlfhj-vWjSGP0al8OW4AHaHa?w=196&h=196&c=7&r=0&o=5&dpr=1.5&pid=1.7"}
  ];

  return (
    <div className="order">
      <h1>Order Details</h1>
      <div className="cart-items">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <img src={item.imageurl} alt={item.name} style={{ width: "100px", height: "100px" }} />
            <div className="cart-item-details">
              <h3>Name: {item.name}</h3>
              <p>Description: {item.description}</p>
              <p>Price: ${item.price}</p>
            </div>
          </div>
        ))}
      </div>
      <Link to="/product/cart">
        <button>Back to Cart</button>
      </Link>
    </div>
  );
};

export default Order;
