// Order.js
import React from 'react';

const Order = (props) => {
  // Check if props.location or props.location.state is undefined
  if (!props.location || !props.location.state) {
    return (
      <div>
        <h2>Error</h2>
        <p>Location state is undefined</p>
      </div>
    );
  }

  // Destructure cartItems from props.location.state with default value
  const { cartItems = [] } = props.location.state;

  return (
    <div>
      <h2>Ordered Items</h2>
      {cartItems.length > 0 ? (
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              <p>Name: {item.name}</p>
              <p>Description: {item.description}</p>
              <p>Price: ${item.price}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No items ordered</p>
      )}
    </div>
  );
};

export default Order;
