import React, { useState } from 'react';
import './App.css';

function App() {
  const [expandedCard, setExpandedCard] = useState(null);

  // Function to handle card click and expand
  const handleCardClick = (cardIndex) => {
    setExpandedCard(expandedCard === cardIndex ? null : cardIndex);
  };

  // Sample data for cards
  const cardsData = [
    { id: 1, title: 'Product 1', description: 'Lorem ipsum dolor sit amet.', price: 100, discount: 10, rating: 4.5 },
    { id: 2, title: 'Product 2', description: 'Consectetur adipiscing elit.', price: 150, discount: 15, rating: 4.2 },
    { id: 3, title: 'Product 3', description: 'Sed do eiusmod tempor incididunt.', price: 200, discount: 20, rating: 4.7 },
    { id: 4, title: 'Product 4', description: 'Ut labore et dolore magna aliqua.', price: 120, discount: 12, rating: 4.0 },
    { id: 5, title: 'Product 5', description: 'Ut enim ad minim veniam.', price: 180, discount: 18, rating: 4.8 },
    { id: 6, title: 'Product 6', description: 'Quis nostrud exercitation ullamco.', price: 220, discount: 22, rating: 4.3 },
  ];

  return (
    <div className='App'>
      <div className='topCards'>
        {cardsData.slice(0, 3).map((card, index) => (
          <div key={card.id} className={`card ${expandedCard === index ? 'expanded' : ''}`} onClick={() => handleCardClick(index)}>
            <h2>{card.title}</h2>
            <p>Price: ${card.price}</p>
            {expandedCard === index && (
              <div className='expandedInfo'>
                <p>Description: {card.description}</p>
                <p>MRP: ${card.price + card.discount}</p>
                <p>Discount: {card.discount}%</p>
                <p>You Save: ${card.discount}</p>
                <p>Ratings: {card.rating}</p>
              </div>
            )}
          </div>
        ))}
      </div>
      <div className='bottomCards'>
        {cardsData.slice(3).map((card, index) => (
          <div key={card.id} className={`card ${expandedCard === index + 3 ? 'expanded' : ''}`} onClick={() => handleCardClick(index + 3)}>
            <h2>{card.title}</h2>
            <p>Price: ${card.price}</p>
            {expandedCard === index + 3 && (
              <div className='expandedInfo'>
                <p>Description: {card.description}</p>
                <p>MRP: ${card.price + card.discount}</p>
                <p>Discount: {card.discount}%</p>
                <p>You Save: ${card.discount}</p>
                <p>Ratings: {card.rating}</p>
              </div>
            )}
          </div>
        ))}
      </div>
      <div className='buttons'>
        <button>Button 1</button>
        <button>Button 2</button>
      </div>
    </div>
  );
}

export default App;
