import React, { useState } from 'react';
import products from './products';
import '../component/styles.css'

// Define symbols for payment cards
const CardSymbols = {
  VISA: 'ðŸ’³',
  MASTERCARD: 'ðŸ’³',
  AMEX: 'ðŸ’³',
  DISCOVER: 'ðŸ’³',
};

const OrderComponent = () => {
  const [paymentMethod, setPaymentMethod] = useState(null);
  const [customerName, setCustomerName] = useState('');
  const [customerAddress, setCustomerAddress] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handlePaymentMethodSelect = (method) => {
    setPaymentMethod(method);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!paymentMethod || !customerName || !customerAddress) {
      setErrorMessage('Please fill out all fields.');
      return;
    }
    // Perform order submission logic here
    // For demonstration, we're showing an alert
    window.alert('Order placed successfully!');
    // Redirect to products page
    window.location.href = '/product';
    // Reset form fields and error message
    setPaymentMethod(null);
    setCustomerName('');
    setCustomerAddress('');
    setErrorMessage('');
  };

  return (
    <div style={{ color: 'white' }}>
      <h2>Order Summary</h2>
      <p>Total: ${products.totalprice}</p>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="paymentMethod">Payment Method:</label>
          <select id="paymentMethod" value={paymentMethod} onChange={(e) => handlePaymentMethodSelect(e.target.value)}>
            <option value="">Select Payment Method</option>
            <option value="VISA">Visa</option>
            <option value="MASTERCARD">Mastercard</option>
            <option value="AMEX">American Express</option>
            <option value="DISCOVER">Discover</option>
          </select>
        </div><br/>
        <div>
          <label htmlFor="customerName">Customer Name:</label>
          <input type="text" id="customerName" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
        </div><br/>
        <div>
          <label htmlFor="customerAddress">Customer Address:</label>
          <input type="text" id="customerAddress" value={customerAddress} onChange={(e) => setCustomerAddress(e.target.value)} />
        </div><br/>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        <button type="submit" className='button-submit'>Submit</button>
      </form>
    </div>
  );
};

export default OrderComponent;
