import React, { useState } from 'react';

const OrderComponent = () => {
  const [paymentMethod, setPaymentMethod] = useState(null);

  const handlePaymentMethodSelect = (method) => {
    setPaymentMethod(method);
  };

  // Define symbols for payment cards
  const CardSymbols = {
    VISA: 'ðŸ’³',
    MASTERCARD: 'ðŸ’³',
    AMEX: 'ðŸ’³',
    DISCOVER: 'ðŸ’³',
  };

  // Render the card symbol based on the selected payment method
  const renderCardSymbol = () => {
    if (paymentMethod) {
      return CardSymbols[paymentMethod];
    }
    return null;
  };

  return (
    <div>
      <h2>Order Summary</h2>
      <p>Total: $100</p>
      <h3>Select Payment Method:</h3>
      <div>
        <button onClick={() => handlePaymentMethodSelect('VISA')}>Visa</button>
        <button onClick={() => handlePaymentMethodSelect('MASTERCARD')}>Mastercard</button>
        <button onClick={() => handlePaymentMethodSelect('AMEX')}>American Express</button>
        <button onClick={() => handlePaymentMethodSelect('DISCOVER')}>Discover</button>
      </div>
      {paymentMethod && (
        <div>
          <h3>Selected Payment Method:</h3>
          <p>{renderCardSymbol()} {paymentMethod}</p>
          {/* Add payment form or further steps here */}
        </div>
      )}
    </div>
  );
};

export default OrderComponent;
