import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import products from './products';
const Product = ({ product }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [quantity, setQuantity] = useState(1); // State for quantity
  const [cartImage, setCartImage] = useState(null); // State to store image URL for the cart

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  const addToCart = () => {
    // Logic to add product to cart
    const cartItem = {
      ...product,
      quantity: quantity
    };
    // You can dispatch an action to add the cartItem to your cart state
    console.log("Added to cart:", cartItem);
    // Set the cart image URL to the product's image URL
    setCartImage(product.imageUrl);
  };

  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value));
  };

  return (
    <div className="product">
      <img src={product.imageUrl} product={products.id}alt="Product Image" onClick={toggleDetails} />
      {showDetails && (
        <div>
          <h3>{product.name}</h3>
          <p>{product.description}</p>
          <p>Price: ${product.price}</p>
          <div>
            {/* Quantity dropdown */}
            <select value={quantity} onChange={handleQuantityChange}>
              {[...Array(10)].map((_, index) => (
                <option key={index} value={index + 1}>{index + 1}</option>
              ))}
            </select>
            {/* Add to Cart button */}
            <button onClick={addToCart}>Add to Cart</button>
            {/* Place Order button */}
            <Link to="/product/order">
              <button>Place Order</button>
            </Link>
          </div>
        </div>
      )}
      {/* Display the cart image if set */}
      {cartImage && <img src={cartImage} alt="Cart Image" />}
    </div>
  );
};

export default Product;
