import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  // Function to fetch cart items from the JSON server
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get('http://localhost:8888/products');
        setCartItems(response.data);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };

    fetchCartItems();
  }, []);

  // Function to remove a product from the cart
  const removeProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:8888/products/${id}`);
      const updatedCart = cartItems.filter(item => item.id !== id);
      setCartItems(updatedCart);
    } catch (error) {
      console.error('Error removing product:', error);
    }
  };

  // Function to add a product to the cart from an image click
  const addToCartFromImage = async (product) => {
    try {
      await axios.post('http://localhost:8888/cartItems', product);
      setCartItems([...cartItems, product]);
    } catch (error) {
      console.error('Error adding product to cart:', error);
    }
  };

  // Calculate total price
  const totalPrice = cartItems.reduce((acc, item) => acc + item.price, 0);

  return (
    <div className="cart">
      {cartItems.map((item) => (
        <div key={item.id} className="cart-item">
          <img
            src={item.imageurl}
            alt={item.name}
            style={{ width: "23%", height: "23%" }}
            onClick={() => addToCartFromImage(item)} // Add click handler to add item to cart
          />
          <div className="cart-item-details" style={{ color: "white" }}>
            <h3>Name: {item.name}</h3>
            <p>Description: {item.description}</p>
            <p>Price: ${item.price}</p>
            <button onClick={() => removeProduct(item.id)}>Remove</button>
          </div>
        </div>
      ))}
      <div className="total-price">
        Total Price: ${totalPrice}
      </div>
    </div>
  );
};

export default Cart;
