import React, { useState } from 'react';

// Define symbols for payment cards
const CardSymbols = {
  VISA: 'ðŸ’³',
  MASTERCARD: 'ðŸ’³',
  AMEX: 'ðŸ’³',
  DISCOVER: 'ðŸ’³',
};

const OrderComponent = () => {
  const [paymentMethod, setPaymentMethod] = useState(null);

  const handlePaymentMethodSelect = (method) => {
    setPaymentMethod(method);
  };

  return (
    <div>
      <h2>Order Summary</h2>
      <p>Total: $100</p>
      <h3>Select Payment Method:</h3>
      <div>
        <button onClick={() => handlePaymentMethodSelect('VISA')}>
          {CardSymbols.VISA} Visa
        </button>
        <button onClick={() => handlePaymentMethodSelect('MASTERCARD')}>
          {CardSymbols.MASTERCARD} Mastercard
        </button>
        <button onClick={() => handlePaymentMethodSelect('AMEX')}>
          {CardSymbols.AMEX} American Express
        </button>
        <button onClick={() => handlePaymentMethodSelect('DISCOVER')}>
          {CardSymbols.DISCOVER} Discover
        </button>
      </div>
      {paymentMethod && (
        <div>
          <h3>Selected Payment Method:</h3>
          <p>{CardSymbols[paymentMethod]} {paymentMethod}</p>
          {/* Add payment form or further steps here */}
        </div>
      )}
    </div>
  );
};

export default OrderComponent;
