import React,{useState} from 'react';
import '../App.css'; // Assuming you have a CSS file for styling

// Define an array of image URLs for the cards

import './App.css'; // Assuming you have a CSS file for styling

// Define an array of image URLs for the cards
const cardImages = [
  "image1.jpg",
  "image2.jpg",
  "image3.jpg",
  "image4.jpg",
  "image5.jpg",
  "image6.jpg"
];

function App() {
  return (
    <div className="app">
      <div className="top-row">
        <Card imageUrl={cardImages[0]} />
        <Card imageUrl={cardImages[1]} />
        <Card imageUrl={cardImages[2]} />
      </div>
      <div className="bottom-row">
        <Card imageUrl={cardImages[3]} />
        <Card imageUrl={cardImages[4]} />
        <Card imageUrl={cardImages[5]} />
      </div>
    </div>
  );
}

function Card({ imageUrl }) {
  const [showDetails, setShowDetails] = useState(false);

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  return (
    <div className="card" onClick={toggleDetails}>
      <img src={imageUrl} alt="card" />
      {showDetails && (
        <div className="details">
          <p>Description: Lorem ipsum dolor sit amet</p>
          <p>MRP: $20</p>
          <p>Discount: 10%</p>
          <div className="buttons">
            <button>Add to Cart</button>
            <button>Order</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
