import React from 'react';
import './App.css';
import { BrowserRouter as Router,Route,Routes} from 'react-router-dom';
import ProductGrid from './component/ProductGrid';
// import products from './component/products';
import background from './assets/images/background.jpg'
import Header from './component/Header';
import Footer from './component/Footer';
import Home from './component/Home';
import Cart from './component/Cart';
import Order from './component/Order';






function App() {
  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh', // Ensure the background covers the entire viewport height
    padding: '20px' // Add padding to your content
  };
  return (
    <Router>

    <div className='App' style={containerStyle}>
       <h1 style={{color:"orange",textAlign:"center"}}>PRODUCTS</h1>
       <Header/>
      {/* <ProductGrid products={products}/> */}
      <Routes>
       {/* <Route path="/" exact component={Home}/> */}
       <Route path="/product"component={ProductGrid}/>
       <Route path="/product/cart"component={Cart}/>
       <Route path="/product/order"component={Order}/>

      </Routes>

      <Footer/>
    </div>
    </Router>
  );
}
export default App;
